<?function strFrmat($string){	$string = addslashes(stripslashes($string));	    $string = trim($string);    return $string;}function addRecord($table, $record, $printquery = 0){	$k = 0;	foreach($record as $key => $val) 	{ 		if (trim($val) != 'Array')		{		$FIELDS[$k] = "`".$key."`";			$VALUES[$k] = "'".strFrmat($val)."'";			$k++;		}    }		$txtFIELDS= implode (',' , $FIELDS);	$txtVALUES= implode (',' , $VALUES);	$query = "insert into ".$table." (".$txtFIELDS.") values (".$txtVALUES.")";    $result = mysql_query($query);    if ($printquery == 1) echo "<hr> <b>QUERY :</b> <br> ".$query."<hr>";      if ($result)    return mysql_insert_id();    else return 0;}?>